mapper.select:
select
`menu_id`,`parent_id`,`name`,`url`,`perms`,`type`,`icon`,`order_num`,`create_time`,`update_time`,`create_by`,`update_by`
from sys_menu

	 and menu_id = #{menuId} 
	 and parent_id = #{parentId} 
	 and name = #{name} 
	 and url = #{url} 
	 and perms = #{perms} 
	 and type = #{type} 
	 and icon = #{icon} 
	 and order_num = #{orderNum} 
	 and create_time = #{createTime} 
	 and update_time = #{updateTime} 
	 and create_by = #{createBy} 
	 and update_by = #{updateBy} 



		order by ${sort} ${order}


		order by menu_id desc



	limit #{offset}, #{limit}
